function apagaAbas(){document.querySelectorAll("[data-tab-id]").forEach(function(t){t.classList.remove("characters__display--is-active")})}function apagaBordaBotoes(){document.querySelectorAll("[data-tab-button]").forEach(function(t){t.classList.remove("characters__tabs__button--is-active")})}document.addEventListener("DOMContentLoaded",function(){this.querySelectorAll("[data-tab-button]").forEach(function(t){t.addEventListener("click",function(t){apagaBordaBotoes(),t.target.classList.add("characters__tabs__button--is-active");t=t.target.dataset.tabButton,console.log(t),t=document.querySelector(`[data-tab-id=${t}]`);apagaAbas(),t.classList.add("characters__display--is-active")})})});